mapper.select:
select board_num, user_id, title, hit, date_format(write_date,'%m-%d %H:%i') write_date from board where type_num=4
where title like '%${searchWord}%'
order by board_num desc limit ${onePageRecord} offset ${offsetIndex}

select board_num, user_id, title, hit, date_format(write_date,'%m-%d %H:%i') write_date from board where type_num=5
where title like '%${searchWord}%'
order by board_num desc limit ${onePageRecord} offset ${offsetIndex}
